/* Styles for dashboard */
body {
  background-color: #000; 
  color: #fff;
  font-family: 'Arial', sans-serif;
  margin: 0;
  padding: 0;
}

/* Header styles */
header {
  background-color: #121212; /* spot background color */
  padding: 20px;
  text-align: center;
}

header h1 {
  font-size: 36px;
  color: #1DB954; /* spot green */
  margin: 0;
}

/* Container styles */
.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  display: flex;
}

/* Sidebar styles */
.sidebar {
  width: 20%;
  background-color: rgba(18, 18, 18, 1); /* box color */
  color: #fff; /* White text */
  padding: 20px;
  box-sizing: border-box;
  height: 100vh;
  position: fixed;
  border-radius: 20px; /* rounded corners*/
  border-right: 2px solid #1DB954; /* green border */
  overflow-y: auto; /* vertical scrolling */
  overflow-x: hidden;
}

/* Floating effect */
.sidebar::before {
  content: "";
  position: absolute;
  top: 0;
  left: -20px;
  width: 40px;
  height: 100%;
  background: rgba(18, 18, 18, 1); 
  border-radius: 50%;
}

.sidebar::after {
  content: "";
  position: absolute;
  top: 0;
  right: -20px;
  width: 40px;
  height: 100%;
  background: rgba(18, 18, 18, 1);  
  border-radius: 50%;
}

/* Customize scrollbar */
.sidebar::-webkit-scrollbar {
  width: 8px;
}

.sidebar::-webkit-scrollbar-track {
  background-color: rgba(18, 18, 18, 1);
}

.sidebar::-webkit-scrollbar-thumb {
  background-color: #1DB954;
  border-radius: 4px;
}

/* Playlist list styles */
.playlist-list {
  list-style: none;
  padding: 0;
}

/* Playlist item styles */
.playlist-item {
  margin-bottom: 10px;
  display: flex;
  align-items: center;
  padding: 5px;
  border: 0px solid #1DB954; /* Green border around each playlist */
  border-radius: 5px;
  background-color: #333; /* Darker background color */
}

/* Playlist icon styles */
.playlist-item img {
  width: 60px;
  height: 60px;
  margin-right: 10px;
  border-radius: 10%; /* Make the playlist icons circular */
  border: 0px solid #1DB954; /* Green border around the icons */
}

/* Playlist link styles */
.playlist-item a {
  color: #fff; /* White text */
  text-decoration: none;
}

.playlist-item a:hover {
  color: #1DB954; /* Green text on hover */
}

/* Heading styles */
h2 {
  font-size: 24px;
  margin-bottom: 20px;
  color: #fff; /* Green text */
}

.featured-playlists {
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-start; /* Change to flex-start to left-align cards */
  margin-left: 20px; /* Adjust margin for spacing on the left */
  margin-top: 20px; /* Add margin to the top */
}

/* Playlist card styles */
.playlist-card {
  background-color: #333;
  border-radius: 8px;
  padding: 20px;
  margin: 10px 10px 10px 0; /* Adjust margin for spacing between cards */
  flex-basis: calc(33.33% - 20px);
  box-sizing: border-box;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s ease;
  cursor: pointer;
  overflow: hidden;
}

.playlist-card:nth-child(3n) {
  margin-right: 0;
}

.playlist-card:hover {
  transform: translateY(-5px);
}


/* Playlist image styles */
.playlist-card img {
  width: 100%;
  border-radius: 4px;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);
}

/* Playlist title styles */
.playlist-title {
  margin-top: 10px;
  font-size: 18px;
  font-weight: bold;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
  color: #fff; /* White text */
}

/* Spotify Green color for links and buttons */
a, button {
  color: #1DB954;
  text-decoration: none;
  border: none;
  background: none;
  cursor: pointer;
  transition: color 0.2s ease;
}

a:hover, button:hover {
  color: #169846; /* Lighter green on hover */
}

/* Center align text */
.text-center {
  text-align: center;
}

/* Search bar styles */
.search-container {
  display: flex;
  align-items: center;
  margin-bottom: 20px;
}

#searchInput {
  flex-grow: 1;
  padding: 10px;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  background-color: #333;
  color: #fff;
}

#searchButton {
  background-color: #1DB954;
  color: #fff;
  border: none;
  border-radius: 4px;
  padding: 10px 20px;
  margin-left: 10px;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

#searchButton:hover {
  background-color: #169846; /* Lighter green on hover */
}

